// 1) Recoge todos los items en un array
const allItems = items;

// 2) Extrae el texto del usuario (campo chatInput)
const userItem = allItems.find(i => i.json.chatInput !== undefined);
const userText = userItem ? userItem.json.chatInput : "";

// 3) Filtra los chunks del documento (campo pageContent)
const docChunks = allItems
  .filter(i => i.json.pageContent !== undefined && i.json.chatInput === undefined)
  .map(i => i.json.pageContent);

// 4) Une todos los trozos en un solo string
const docContent = docChunks.join("\n\n");

// 5) Construye el prompt final
const fullPrompt = docChunks.length > 0
  ? `${userText}\n\n--- este es el Contenido del archivo subido por el usuario a verificar por el cumplimiento de la normativa que tienes en tu base de conocimiento ---\n\n${docContent}`
  : userText;

// 6) Devuelve un 煤nico item con la propiedad prompt
return [{
  json: { prompt: fullPrompt }
}];

