:root {
  /* Fuente */
  --chat--font-family: 'Roboto', sans-serif;

  /* Colores */
  --chat--color-primary: #0d47a1;               /* Azul corporativo */
  --chat--color-primary-shade-50: #0b3d91;
  --chat--color-primary-shade-100: #092f7a;
  --chat--color-secondary: #607d8b;             /* Gris azulado */
  --chat--color-secondary-shade-50: #546e78;
  --chat--color-white: #ffffff;
  --chat--color-light: #1e2530;                 /* Fondo principal oscuro */
  --chat--color-light-shade-50: #2a313e;
  --chat--color-light-shade-100: #3b4554;
  --chat--color-medium: #4f5970;                /* Gris medio para bordes/textos */
  --chat--color-dark: #cfd8e3;                  /* Texto claro */
  --chat--color-disabled: #777980;
  --chat--color-typing: #9ea7b8;

  /* Espaciado y bordes */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.375rem;
  --chat--transition-duration: 0.2s;

  /* Ventana */
  --chat--window--width: 400px;
  --chat--window--height: 600px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 1000;
  --chat--window--border: 1px solid var(--chat--color-light-shade-50);
  --chat--window--border-radius: var(--chat--border-radius);

  /* Cabecera */
  --chat--header--background: var(--chat--color-primary);
  --chat--header--color: var(--chat--color-white);
  --chat--header--padding: var(--chat--spacing);
  --chat--heading--font-size: 1.75em;
  --chat--subtitle--font-size: 1em;

  /* Mensajes */
  --chat--messages-list--padding: var(--chat--spacing);
  --chat--message--font-size: 1rem;
  --chat--message--padding: var(--chat--spacing);
  --chat--message--border-radius: var(--chat--border-radius);
  --chat--message-line-height: 1.6;
  --chat--message--margin-bottom: var(--chat--spacing);

  --chat--message--bot--background: var(--chat--color-light-shade-50);
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--bot--border: none;

  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--user--border: none;

  --chat--message--pre--background: rgba(255, 255, 255, 0.05);

  /* Toggle */
  --chat--toggle--size: 60px;
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);
  --chat--toggle--color: var(--chat--color-white);

  /* rea de entrada */
  --chat--textarea--height: 48px;
  --chat--textarea--max-height: 28rem;
  --chat--input--font-size: 1rem;
  --chat--input--border: 1px solid var(--chat--color-medium);
  --chat--input--border-radius: var(--chat--border-radius);
  --chat--input--padding: 0.75rem;
  --chat--input--background: var(--chat--color-light);
  --chat--input--text-color: var(--chat--color-dark);
  --chat--input--line-height: 1.5;
  --chat--input--placeholder--font-size: var(--chat--input--font-size);
  --chat--input--border-active: 1px solid var(--chat--color-primary);
  --chat--input--left--panel--width: 2.5rem;

  /* Botones */
  --chat--button--color: var(--chat--color-white);
  --chat--button--background: var(--chat--color-primary);
  --chat--button--padding: 0.5rem 1rem;
  --chat--button--border-radius: var(--chat--border-radius);
  --chat--button--hover--background: var(--chat--color-primary-shade-50);

  /* Enviar y archivo */
  --chat--input--send--button--background: var(--chat--color-primary);
  --chat--input--send--button--color: var(--chat--color-white);
  --chat--input--send--button--background-hover: var(--chat--color-primary-shade-50);

  --chat--input--file--button--background: var(--chat--color-light-shade-50);
  --chat--input--file--button--color: var(--chat--color-medium);
  --chat--input--file--button--background-hover: var(--chat--color-light-shade-100);
  --chat--input--file--button--color-hover: var(--chat--color-secondary-shade-50);

  /* Cuerpo y pie */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-light);
  --chat--footer--color: var(--chat--color-dark);
}

/* Override para ancho de mensaje */
.chat-message {
  max-width: 60%;
}
